import React, { useState } from "react";
import './ExpenseForm.css';

function ExpenseForm() {

    // const [enteredTitle, setEnteredTitle] = useState('');
    // const [enteredAmount, setEnteredAmount] = useState('');
    // const [enteredDate, setEnteredDate] = useState('');

    const titleChangeHandler = (event) => setEnteredTitle(event.target.value);
    const amountChangeHandler = (event) => setEnteredAmount(event.target.value);
    const dateChangeHandler = (event) => setEnteredDate(event.target.value);

    return (
        <form>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label htmlFor="title">Title</label>
                    <input type="text" id="title" onChange={titleChangeHandler}></input>
                </div>
                <div className="new-expense__control">
                    <label htmlFor="amount">Amount</label>
                    <input type="number" id="amount" min="0.01" max="0.01" onChange={amountChangeHandler}></input>
                </div>
                <div className="new-expense__control">
                    <label htmlFor="date">Date</label>
                    <input type="date" id="date" min="2019-01-01" max="31-12-2022" onChange={dateChangeHandler}></input>
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="submit">Add</button>
            </div>
        </form>);
}

export default ExpenseForm;
